import React, { Component } from 'react'
import Header from '../components/Header.js'
// import necessary components from reactstrap
import {
  Col,
  Container,
  ListGroup,
  ListGroupItem,
  Row
} from 'reactstrap'
import {
  BrowserRouter as Router,
  NavLink,
  Route
} from 'react-router-dom'
import mockCats from '../mockCats.js'

class CatIndex extends Component{
  constructor(props){
    super(props)
    this.state = {
      cats: mockCats
    }
  }
  render(){
    return(
      <Router>
        <Header />
        <Container>
          <Row>
            <Col>
              <ListGroup>
                { this.state.cats.map((cat, index) => {
                  return (
                    <ListGroupItem key={ index }>
                      <h4>
                        {/* using dot notation to access the cat attributes */}
                        <NavLink
                          className='cat-name'
                          to={`/catshow/${cat.id}`}
                        >
                          { cat.name }
                        </NavLink>
                      </h4>
                    </ListGroupItem>
                  )
                })}
              </ListGroup>
            </Col>
          </Row>
        </Container>
        <Route exact path={`/catshow/${cat.id}`} render={(props) => <CatShow id={cat.id} /> } />
      </Router>
    )
  }
}
export default CatIndex
