import React, { Component } from 'react'
import Home from './pages/Home'
// imports routing components
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom'
import './App.css'

class App extends Component{
  render(){
    return(
      <React.Fragment>
        <Home />
        <Router>
          <Switch>
          {/* Calls the CatIndex component and passes the state object of all the cats */}
            <Route
              exact path="/newcat"
              // updated handleSubmit to pass the new createCat method to NewCat.js
              render={ (props) => <NewCat handleSubmit={ this.createCat } /> }
            />
            <Route
              exact path="/catindex"
              render={ (props) => <CatIndex cats={ this.state.cats } /> }
            />
          </Switch>
        </Router>
      </React.Fragment>
    )
  }
}
export default App
