import React, { Component } from 'react'
import Home from './pages/Home'
import CatIndex from './pages/CatIndex'
import CatShow from './pages/CatShow'
import CatNew from './pages/CatNew'
import CatEdit from './pages/CatEdit'
// imports routing components
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom'
import './App.css'
// import mockCats from './mockCats.js'

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      cats: []
    }
  }

  componentDidMount(){
    fetch("http://localhost:3000/cats")
    .then((response)=>{
      // checking for a successfull response
      if(response.status === 200){
         // convert the response to json
         // returns a promise
        return(response.json())
      }
    })
    .then((catsArray)=>{
      // set the state with the data from the backend into the empty array
      this.setState({ cats: catsArray })
    })
    .catch(errors => {
      console.log(errors)
    })
  }


  createNewCat = (newcat) => {
    return fetch("http://localhost:3000/cats", {
      // converting an object to a string
      body: JSON.stringify(newcat),
      // specify the info being sent in JSON and the info returning should be JSON
      headers: {
        "Content-Type": "application/json"
      },
      // HTTP verb so the correct endpoint is invoked on the server
      method: "POST"
    })
    .then((response) => {
      // if the response is good  - redirect to show page
      if(response.status === 200){
        //WIP - HERE!!!
        // alert with confirmation / eager loading, optimistic rendering
        // react router has a redirect
      }
    })
    .catch(errors => {
      console.log(errors)
    })
  }

  deleteCat = (id) => {
    return fetch(`http://localhost:3000/cats/${id}`, {
      headers: {
        "Content-Type": "application/json"
      },
      method: "DELETE"
    })
    .then(response => {
      if(response.status === 200){
        let catIndex = this.state.cats.findIndex(cat => cat.id === id)
        const { cats } = this.state
        cats.splice(catIndex, 1)
        this.setState({ cats: cats})
      }
      return response
    })
    .catch(errors => {
      console.log(errors)
    })
  }

  editCat = (editcat) => {
    return fetch(`http://localhost:3000/cats/${editcat.id}`, {
      // converting an object to a string
      body: JSON.stringify(editcat),
      // specify the info being sent in JSON and the info returning should be JSON
      headers: {
        "Content-Type": "application/json"
      },
      // HTTP verb so the correct endpoint is invoked on the server
      method: "PATCH"
    })
    .then((response) => {
      // if the response is good  - redirect to show page
      if(response.status === 200){
        //WIP - HERE!!!
      }
    })
    .catch(errors => {
      console.log(errors)
    })
  }

  render(){
    return(
      <React.Fragment>
        <Router>
          <Switch>
            {/* Home */}
            <Route path="/" exact component={ Home } />
            {/* Index */}
            <Route
              exact path="/catindex"
              render={ (props) => <CatIndex cats={ this.state.cats } /> }
            />
            {/* Show */}
            <Route
              exact path={"/catshow/:id"}
              render={ (props) => {
                let id = props.match.params.id
                let catIndex = this.state.cats.findIndex(cat => cat.id == id)
                return (
                  <CatShow
                    cat={ this.state.cats[catIndex] }
                    deleteCat={ this.deleteCat }
                  />
                )
              } }
            />
            {/* New */}
            <Route
              exact path="/catnew"
              render={ (props) => <CatNew createNewCat={ this.createNewCat } /> }
            />
            {/* Edit */}
              <Route
                exact path={`/catedit/${cat.id}`}
                render={ (props) => {
                  let id = props.match.params.id
                  let catIndex = this.state.cats.find(cat => cat.id == id)
                  return(
                    <CatEdit
                      editCat={ editCat }
                      cat={ this.state.cats[catIndex] }
                      enjoys={ cat.enjoys }
                    />
                  )
                }}
              />
          </Switch>
        </Router>
      </React.Fragment>
    )
  }
}
export default App
