import React, { Component } from 'react'
import Home from './pages/Home'
import CatIndex from './pages/CatIndex'
import CatShow from './pages/CatShow'
import CatNew from './pages/CatNew'
import CatEdit from './pages/CatEdit'
// imports routing components
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom'
import './App.css'
import mockCats from './mockCats.js'

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      cats: mockCats
    }
  }

  createNewCat = (newcat) => {
    // logging the newcat object to endure it has been created
    console.log(newcat)
  }

  render(){
    return(
      <React.Fragment>
        <Router>
          <Switch>
            {/* Home */}
            <Route path="/" exact component={ Home } />
            {/* Index */}
            <Route
              exact path="/catindex"
              render={ (props) => <CatIndex cats={ this.state.cats } /> }
            />
            {/* Show */}
            {
              this.state.cats.map((cat, index) => {
                return(
                  <Route
                    key={ index }
                    exact path={`/catshow/${cat.id}`}
                    render={ (props) => <CatShow {...props} id={ cat.id } /> }
                  />
                )
              })
            }
            {/* New */}
            <Route path="/catnew" createNewCat={ this.createNewCat } component={ CatNew } />
          </Switch>
        </Router>
      </React.Fragment>
    )
  }
}
export default App
