import React, { Component } from 'react'
import Home from './pages/Home'
import CatIndex from './pages/CatIndex'
import CatShow from './pages/CatShow'
import CatNew from './pages/CatNew'
import CatEdit from './pages/CatEdit'
// imports routing components
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom'
import './App.css'
// import mockCats from './mockCats.js'

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      cats: []
    }
  }

  componentDidMount(){
     // fetch returns a promise
    fetch("http://localhost:3000/cats")
    .then((response)=>{
      // checking for a successfull response
      if(response.status === 200){
         // convert the response to json
         // returns a promise
        return(response.json())
      }
    })
    .then((catsArray)=>{
      // set the state with the data from the backend into the empty array
      this.setState({ cats: catsArray })
    })
    .catch(errors => {
      console.log(errors)
    })
  }


  createNewCat = (newcat) => {
    return fetch("http://localhost:3000/cats", {
      // converting an object to a string
      body: JSON.stringify(newcat),
      // specify the info being sent in JSON and the info returning should be JSON
      headers: {
        "Content-Type": "application/json"
      },
      // HTTP verb so the correct endpoint is invoked on the server
      method: "POST"
    })
    .then((response) => {
      // if the response is good call the getCatIndex method
      if(response.ok){
        return this.getCatIndex()
      }
    })
    .catch(errors => {
      console.log(errors)
    })
}



  editCat = (editcat) => {
    // logging the editcat object to endure it has been updated
    console.log(editcat)
  }

  render(){
    return(
      <React.Fragment>
        <Router>
          <Switch>
            {/* Home */}
            <Route path="/" exact component={ Home } />
            {/* Index */}
            <Route
              exact path="/catindex"
              render={ (props) => <CatIndex cats={ this.state.cats } /> }
            />
            {/* Show */}
            {
              this.state.cats.map((cat, index) => {
                return(
                  <Route
                    key={ index }
                    exact path={`/catshow/${cat.id}`}
                    render={ (props) => <CatShow id={ cat.id } /> }
                  />
                )
              })
            }
            {/* New */}
            <Route
              exact path="/catnew"
              render={ (props) => <CatNew createNewCat={ this.createNewCat } /> }
            />
            {/* Edit */}
            {
              this.state.cats.map((cat, index) => {
                return(
                  <Route
                    key={ index }
                    exact path={`/catedit/${cat.id}`}
                    render={ (props) => <CatEdit editCat={ this.editCat } id={ cat.id } /> }
                  />
                )
              })
            }

          </Switch>
        </Router>
      </React.Fragment>
    )
  }
}
export default App
