import React, { Component } from 'react'
import Header from '../components/Header.js'
// import necessary components from reactstrap
import {
  Col,
  Container,
  ListGroup,
  ListGroupItem,
  Row
} from 'reactstrap'
import { BrowserRouter as Router, NavLink } from 'react-router-dom'
import mockCats from '../mockCats.js'

class CatIndex extends Component{
  constructor(props){
    super(props)
    this.state = {
      cats: mockCats
    }
  }
  render(){
    return(
      <React.Fragment>
        <Header />
        <Container>
          <Row>
            <Col>
              <ListGroup>
                {/* maping over cats which was passed through the CatIndex component call in App.js */}
                {/* there is no "this" in functional components */}
                { this.state.cats.map((cat, index) => {
                  return (
                    <ListGroupItem key={ index }>
                      <h4>
                        {/* using dot notation to access the cat attributes */}


                        <NavLink className='cat-name' to={`/catshow/${cat.id}`}>{ cat.name }</NavLink> - <small className='cat-age'>{ cat.age } years old</small>
                      </h4>
                    </ListGroupItem>
                  )
                })}
              </ListGroup>
            </Col>
          </Row>
        </Container>
        <Route exact path={`/cats/${cat.id}`} render={(props) => <CatProfile id={cat.id} /> } />
      </React.Fragment>
    )
  }
}
export default CatIndex
