import React, { Component } from 'react'
import Header from '../components/Header.js'
import Footer from '../components/Footer.js'
import { NavLink } from 'react-router-dom'
// import mockCats from '../mockCats.js'

class CatShow extends Component{
  constructor(props){
    super(props)
    this.state = {
      cat: null,
      success: false
    }
  }

  componentDidMount(){
     // fetch returns a promise
    fetch(`http://localhost:3000/cats/${this.props.id}`)
    .then((response)=>{
      // checking for a successfull response
      if(response.status === 200){
         // convert the response to json
         // returns a promise
        return(response.json())
      }
    })
    .then((oneCat)=>{
      // set the state with the data from the backend
      this.setState({ cat: oneCat })
    })
    .catch(errors => {
      console.log(errors)
    })
  }
  //
  // deleteCat = (id) => {
  //   return fetch(`http://localhost:3000/cats/${id}`, {
  //     headers: {
  //       "Content-Type": "application/json"
  //     },
  //     method: "DELETE"
  //   })
  //   .then(() => {
  //     this.setState({ success: true })
  //   })
  // }

  render(){
    const { cat } = this.state
    return(
      <React.Fragment>
        <Header />
          { this.state.cat &&
            <div>
              <h4>Hi, my name is <strong>{ cat.name }</strong>!</h4>
              <p>I am { cat.age }</p>
              <p>I enjoy { cat.enjoys }</p>
              <NavLink
                to={`/catedit/${cat.id}`}
              >
                Edit Cat Profile
              </NavLink>
              <br />
              <NavLink
                to={ '/cats' }
                onClick={ () => {console.log("here")} }
              >
                Delete Cat Profile
              </NavLink>
            </div>
        }
        <Footer />
      </React.Fragment>
    )
  }
}
export default CatShow
