import React, { Component } from 'react'
import Header from '../components/Header.js'

class CatShow extends Component{
  render(){
    // Accessing the id from the URL
    const { id } = this.props.match.params
    // Using .find() to return the correct object from the data set
    console.log("id:", id)
    console.log("cats:", this.props.cats)
    const cat = this.props.cats.find((cat) => cat.id === parseInt(id))
    return(
      <React.Fragment>
        <Header />
          <h4>Hi, there, { cat.name }!</h4>
          <p>Age: { cat.age }</p>
          <p>Enjoys: { cat.enjoys }</p>
      </React.Fragment>
    )
  }
}
export default CatShow
