import React, { Component } from 'react'
import Header from '../components/Header.js'
import Footer from '../components/Footer.js'
import { NavLink, Redirect } from 'react-router-dom'
// import mockCats from '../mockCats.js'

class CatShow extends Component{
  constructor(props){
    super(props)
    this.state = {
      cat: null,
      success: false
    }
  }

  componentDidMount(){
    if(this.state.success === false){
     // fetch returns a promise
    fetch(`http://localhost:3000/cats/${this.props.id}`)
    .then((response)=>{
      // checking for a successfull response
      if(response.status === 200){
         // convert the response to json
         // returns a promise
        return(response.json())
      }
    })
    .then((oneCat)=>{
      // set the state with the data from the backend
      this.setState({ cat: oneCat })
    })
    .catch(errors => {
      console.log(errors)
    })
    }
  }


  render(){
    const { cat } = this.state
    return(
      <React.Fragment>
        <Header />
          { this.state.cat &&
            <div>
              <h4>Hi, my name is <strong>{ cat.name }</strong>!</h4>
              <p>I am { cat.age }</p>
              <p>I enjoy { cat.enjoys }</p>
              <NavLink
                to={`/catedit/${cat.id}`}
              >
                Edit Cat Profile
              </NavLink>
              <br />
              <button
                onClick={ () => { this.props.deleteCat(cat.id)
                  .then(response => {
                    console.log("here!")
                    this.setState({ success: true })
                  })
                } }
              >
                Delete Cat Profile
              </button>
            </div>
        }
        <Footer />
        { this.state.success && <Redirect to="/catindex" />}
      </React.Fragment>
    )
  }
}
export default CatShow
